package main

import "fmt"

func main() {
	var openCnt int
	var closeCnt int
	var curr int
	for i, r := range input {
		switch r {
		case '(':
			openCnt++
			curr++
		case ')':
			closeCnt++
			curr--
		default:
			panic("no open or close")
		}
		if curr == -1 {
			fmt.Printf("entered the basement at pos %d\n", i+1)
		}
	}
	fmt.Printf("open: %d, close: %d, diff: %d\n", openCnt, closeCnt, openCnt-closeCnt)
}

